import React, { Component, Fragment } from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter, Route, } from 'react-router-dom';
import { Layout } from 'antd';
import AppHeader from './components/Header/index';
import List from './components/containers/List/index';
import Detail from './components/containers/Detail/index'
import './style.css';
import 'antd/dist/antd.css';

// 意思是Header=Layout.Header
const { Header, Footer, Content } = Layout;

class App extends Component {
    render () {
        return(
            <div>
                {/* minWidth: 1400保证页面宽度最小1300px，当浏览器缩小时会自动生成滚动条 */}
                <Layout style={{ minWidth: 1400 }}>
                    <Header className="header"><AppHeader /></Header>
                    <Content className="content">
                        <BrowserRouter>
                            <Fragment>
                                <Route path='/' component={List}></Route>
                                <Route path='/detail' component={Detail}></Route>
                            </Fragment>
                        </BrowserRouter>
                    </Content>
                    <Footer className="footer">Footer</Footer>
                </Layout>
          </div>
        )
    }
}

ReactDOM.render(<App />, document.getElementById('root'));
