import React, { Component } from 'react';
import headerLogo from './logo.jpg';
import './style.css';
import { Menu, Icon } from 'antd';


export default class AppHeader extends Component {
    constructor(props) {
        super(props);
        this.state = {
            list: [{
                id: 1,
                icon: 'mail',
                title: 'VOA English',
            }, {
                id: 2,
                icon: 'mail',
                title: 'VOA fast',
            }, {
                id: 3,
                icon: 'mail',
                title: 'VOA English',
            }, {
                id: 4,
                icon: 'mail',
                title: 'VOA English',
            }, {
                id: 5,
                icon: 'mail',
                title: 'VOA English',
            }, {
                id: 6,
                icon: 'mail',
                title: 'VOA English',
            },
            ],
        }
    };

    getMenuItems(){
        return this.state.list.map( item => {
            return (
                <Menu.Item key={item.id}>
                        <Icon type={item.icon} />{item.title}
                </Menu.Item>
            )
        })
    };
    render() {
        return (
            <div>
                <img src={headerLogo} className="app-header-logo" />
                {/* horizontal代表横向展示 */}
                <Menu
                    mode="horizontal"
                    className="app-header-menu"
                >
                    {this.getMenuItems()}
                </Menu>
            </div>
        );
    };
}
